package com.victor.tv;import android.content.Intent;import android.graphics.Color;import android.os.Build;import android.os.Bundle;import android.support.v7.app.AppCompatActivity;import android.view.View;import android.view.Window;import android.view.WindowManager;/** * Created by victor on 2016/2/2. */public class BaseActivity extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        initialize();    }    private void initialize () {        /*if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.KITKAT) {            WindowManager.LayoutParams localLayoutParams = getWindow().getAttributes();            localLayoutParams.flags = (WindowManager.LayoutParams.FLAG_TRANSLUCENT_STATUS | localLayoutParams.flags);        }*/        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.LOLLIPOP) {            Window window = getWindow();            window.clearFlags(WindowManager.LayoutParams.FLAG_TRANSLUCENT_STATUS);            window.getDecorView().setSystemUiVisibility(View.SYSTEM_UI_FLAG_LAYOUT_HIDE_NAVIGATION);            window.addFlags(WindowManager.LayoutParams.FLAG_DRAWS_SYSTEM_BAR_BACKGROUNDS);            window.setStatusBarColor(Color.TRANSPARENT);        }    }    protected String getName() {        return BaseActivity.class.getName();    }    @Override    public void onBackPressed() {        super.onBackPressed();        overridePendingTransition(R.anim.right_in_back, R.anim.right_out_back);    }    @Override    public void startActivity(Intent intent) {        super.startActivity(intent);        overridePendingTransition(R.anim.right_in, R.anim.right_out);    }    @Override    protected void onDestroy() {        super.onDestroy();    }}