package com.victor.tv.fragment;import android.content.Intent;import android.os.Bundle;import android.view.View;import com.victor.tv.FilmPosterActivity;import com.victor.tv.LiveActivity;import com.victor.tv.R;import com.victor.tv.SearchVideoActivity;import com.victor.tv.VideoActivity;import butterknife.OnClick;public class RecommendFragment extends BaseFragment {    @Override    protected int getLayoutResource() {        return R.layout.test_page1;    }    @Override    public void onActivityCreated(Bundle savedInstanceState) {        super.onActivityCreated(savedInstanceState);    }    @OnClick(R.id.page1_item1)    public void onLiveClick (View view) {        startActivity(new Intent(getActivity(), LiveActivity.class));    }    @OnClick(R.id.page1_item2)    public void onFilmPosterClick (View view) {        startActivity(new Intent(getActivity(), FilmPosterActivity.class));    }    @OnClick(R.id.page1_item3)    public void onVideoClick (View view) {        startActivity(new Intent(getActivity(), VideoActivity.class));    }    @OnClick(R.id.page1_item4)    public void onSearchVideoClick (View view) {        startActivity(new Intent(getActivity(), SearchVideoActivity.class));    }}